#!/usr/bin/python
# -*- coding: UTF-8 -*-

# 导入模块
import pandas as pd

def main():
    df1 = pd.DataFrame({'a': [12, 23, 34, 45], 'b': [4, 4, 2, 1]})
    for i in range(len(df1)):
        print(i)
    df2 = pd.DataFrame({'c': [1, 2, 3, 4], 'd': [0, 0, 0, 0], 'e': [0,0,0,0], 'f': [0,0,0,0]})

    for index, row in df1.iterrows():
        if row['b'] >3:
            df2.loc[df2['c'] == int(row['a']/10),'d'] += 1
        elif 2< row['b'] <=3:
            df2.loc[df2['c'] == int(row['a']/10),'e'] += 1
        elif 1< row['b'] <=2:
            df2.loc[df2['c'] == int(row['a']/10),'f'] += 1

    print(df2)

if __name__ == "__main__":
    main()

