    //解析K线数据，将其转化成{ x轴,
    //                       股价（最高、最低、开盘、收盘）,
    //                       量能}
    export function splitData(rawData) {
            var categoryData = [];
            var values = [];
            var volumes = [];
            for (var i = 0; i < rawData.length; i++) {
                categoryData.push(rawData[i].splice(0, 1)[0]);
                values.push(rawData[i]);
                volumes.push([i, rawData[i][4], rawData[i][1] > rawData[i][0] ? 1 : -1]); //交易量能
            }

            return {
                categoryData: categoryData,
                values: values,
                volumes: volumes
            };
    }

    export function splitDataByType(rawData,isPercent) {
            var categoryData = [];
            var values = [];
            var volumes = [];
            if(false == isPercent)
            {   //只有一个股票时，显示股价蜡烛图
                for (var i = 0; i < rawData.length; i++) {
                    categoryData.push(rawData[i].splice(0, 1)[0]);
                    values.push(rawData[i]);
                    volumes.push([i, rawData[i][4], rawData[i][1] > rawData[i][0] ? 1 : -1]); //交易量能
                }
            }
            else
            {   //多股同列时,显示收盘价相对于第一天开盘价的百分比折线图
                var basicValue=rawData[0][1]; //第一天的开盘价作为基准价
                for (var i = 0; i < rawData.length; i++) {
                    var time = rawData[i][0];
                    categoryData.push(rawData[i].splice(0, 1)[0]);
                    volumes.push([i, rawData[i][4], rawData[i][1] > rawData[i][0] ? 1 : -1]); //交易量能
                    values.push([String(time), (rawData[i][1]-basicValue)*100/basicValue]);   //收盘价相对于第一天开盘价的涨幅
                }
            }

            return {
                categoryData: categoryData,
                values: values,
                volumes: volumes
            };
    }

    export function calculateMA(dayCount, data) {
            var result = [];
            for (var i = 0, len = data.values.length; i < len; i++) {
                if (i < dayCount) {
                    result.push('-');
                    continue;
                }
                var sum = 0;
                for (var j = 0; j < dayCount; j++) {
                    sum += data.values[i - j][1];
                }
                result.push(+(sum / dayCount).toFixed(3));
            }
            return result;
    }

