    export function  addYAxis(option, isShow,data)
    {
        var Min = calMin(data);
        var Max = calMax(data);
       var itemShow = {
                   splitNumber: splitNumberY,
                    min: Min,
                    max: Max,
                    interval: (Max - Min) / 5,
                    splitLine: {show: true,
                        lineStyle:{
                            type:'dotted'
                        }
                    },
                    scale: true,
                    gridIndex:0,
                    position: "right",
                    "axisLine":{       //y轴
                      "show":true
                    },
                    "axisTick":{       //y轴刻度线
                      "show":true
                    }
                   };

       var itemHide = {
                    splitNumber: splitNumberY,
                    min: Min,
                    max: Max,
                    interval: (Max - Min) / 5,
                    splitLine: {show: true,
                        lineStyle:{
                            type:'dotted'
                        }
                    },
                    scale: true,
                    gridIndex:0,
                    position: "right",
                    "axisLine":{       //y轴
                      "show":false
                    },
                    "axisTick":{       //y轴刻度线
                      "show":false
                    },
                    axisLabel : {
                        formatter: function(){
                              return "";
                        }
                   }
            };
       if(isShow)
       {
       option.yAxis.push(itemShow);
       }
       else
       {
       option.yAxis.push(itemHide);
       }

       return (option.yAxis.length-1);
    }


    export function resetSeriesData(option)
    {
            option.series.splice(0, option.series.length);
            option.yAxis.splice(3, option.yAxis.length-3);
    }

    export function resetMacroData(option)
    {
        if(option.series.length>6)
        {
            option.series.splice(6, option.series.length-5);
            option.yAxis.splice(2, option.yAxis.length-2);
            option.legend.data.splice(5, option.legend.data.length-5);
        }
    }

    export function makePoints(option, baseData, macroData)
    {
        for (var i = 0; i < macroData.length; i++)
        {
            for(var j=0; j<(baseData.categoryData.length-1);j++)
            {
                if(baseData.categoryData[j]==macroData[i][0] ||
                    (baseData.categoryData[j]<macroData[i][0] && baseData.categoryData[j+1]>macroData[i][0]))
                    {
                       var item={
                                  name: i,
                                   coord: [String(baseData.categoryData[j]), baseData.values[j][0]],
                                   value: macroData[i][1],
                                   itemStyle: {
                                       color: 'rgb(41,60,85)'
                                              }
                              };
                               option.series[0].markPoint.data.push(item);
                    }
            }
        }
    }

    //计算最大值
 export function calMax(arr) {
    let max = 0;

    for(var i=0; i<(arr.length);i++)
    {
          if (max < arr[i][1]) {
            max = arr[i][1];
            }
    }
    return max*1.1;
  }

  //计算最小值
 export function calMin(arr) {
    let min = 0;
    for(var i=0; i<(arr.length);i++)
    {
          if (min > arr[i][1]) {
            min = arr[i][1];
            }
    }
    return min;
  }
